mapper.select:
SELECT
	ID,
	sei,
	mei,
	mei_KANA,
	sei_KANA,
	ZIP1,
	ZIP2,
	ADDRESS_PREFECTURE,
	ADDRESS_CITY,
	ADDRESS_OOAZA,
	ADDRESS_AZA,
	ADDRESS_OTHER,
	TEL1,
	TEL2,
	TEL3,
	MOBILE_TEL1,
	MOBILE_TEL2,
	MOBILE_TEL3,
	EMAIL,
	PASSWORD,
	SETAI_FAMILY,
	SETAI_COUNT,
	SETAI_STATUS,
	SETAI_YEAR,
	SETAI_LOAN,
	INCOME,
	SAVINGS,
	EMPLOYMENT_STATUS,
	COMPANY_NAME,
	COMPANY_ZIP1,
	COMPANY_ZIP2,
	COMPANY_ADDRESS_PREFECTURE,
	COMPANY_ADDRESS_CITY,
	COMPANY_ADDRESS_OOAZA,
	COMPANY_ADDRESS_AZA,
	COMPANY_ADDRESS_OTHER,
	COMPANY_TEL1,
	COMPANY_TEL2,
	COMPANY_TEL3,
	COMPANY_DEPARTMENT,
	COMPANY_INDUSTRY_TYPE,
	COMPANY_WORK_YEARS
FROM
	M_CUSTOMER
WHERE
	DELETE_FLG = '0'

		AND sei = #{sei}


		AND mei = #{mei}


		AND mei_KANA = #{meiKana}


		AND sei_KANA = #seiKana}


		AND ZIP1 = #{zip1}


		AND ZIP2 = #{zip2}


		AND ADDRESS_PREFECTURE = #{addressPrefecture}


		AND ADDRESS_CITY = #{addressCity}


		AND ADDRESS_OOAZA = #{addressOoaza}


		AND ADDRESS_AZA = #{addressAza}


		AND ADDRESS_OTHER = #{addressOther}


		AND TEL1 = #{tel1}


		AND TEL2 = #{tel2}


		AND TEL3 = #{tel3}


		AND MOBILE_TEL1 = #{mobileTel1}


		AND MOBILE_TEL2 = #{mobileTel2}


		AND MOBILE_TEL3 = #{mobileTel3}


		AND EMAIL = #{email}


		AND PASSWORD = #{password}


		AND SETAI_FAMILY = #{setaiFamily}


		AND SETAI_COUNT = #{setaiCount}


		AND SETAI_STATUS = #{setaiStatus}


		AND SETAI_YEAR = #{setaiYear}


		AND SETAI_LOAN = #{setaiLoan}


		AND INCOME = #{income}


		AND SAVINGS = #{savings}


		AND EMPLOYMENT_STATUS = #{employmentStatus}
